// src/components/ChatInterface.tsx
import React, { useState } from 'react';
// Use our typed hooks for Redux:
import { useAppSelector, useAppDispatch } from '../app/hooks';
import { addMessage, ChatMessage } from '../features/chat/chatSlice';
import { Box, Button, TextField, Paper, Typography } from '@mui/material';
import { motion } from 'framer-motion';
import ReactMarkdown from 'react-markdown';
import axios from 'axios';

const ChatInterface: React.FC = () => {
  // Get the conversation from Redux (typed via useAppSelector)
  const conversation = useAppSelector((state) => state.chat.conversation);
  const dispatch = useAppDispatch();

  // Local state for input and streaming
  const [input, setInput] = useState<string>('');
  const [streamingMessage, setStreamingMessage] = useState<string>('');
  const [streaming, setStreaming] = useState<boolean>(false);

  // Function to start voice input (using the Web Speech API)
  const startVoiceInput = () => {
    const SpeechRecognition =
      (window as any).SpeechRecognition || (window as any).webkitSpeechRecognition;
    if (!SpeechRecognition) {
      alert("Your browser does not support speech recognition.");
      return;
    }
    const recognition = new SpeechRecognition();
    recognition.lang = 'en-US';
    recognition.start();
    recognition.onresult = (event: any) => {
      const transcript = event.results[0][0].transcript;
      setInput(transcript);
    };
  };

  // Function to speak text (using the Web Speech API)
  const speakText = (text: string) => {
    const utterance = new SpeechSynthesisUtterance(text);
    window.speechSynthesis.speak(utterance);
  };

  // Handle sending a message using SSE for streaming the answer
  const handleSend = async (e: React.FormEvent<HTMLFormElement>) => {
    e.preventDefault();
    if (!input.trim()) return;

    // Dispatch the user's message to Redux
    dispatch(addMessage({ role: 'user', content: input }));

    // Start streaming: reset streaming state and message
    setStreaming(true);
    setStreamingMessage('');

    // Open an EventSource to the backend's streaming endpoint
    const eventSource = new EventSource(
      `http://localhost:8000/query/stream?query=${encodeURIComponent(input)}`
    );

    // Listen for streamed messages
    eventSource.onmessage = (event) => {
      if (event.data === "[DONE]") {
        // When stream is finished, close the connection and dispatch the final answer
        eventSource.close();
        dispatch(addMessage({ role: 'assistant', content: streamingMessage }));
        setStreaming(false);
        setStreamingMessage(''); // Clear the temporary streaming text
      } else {
        // Append each data chunk to the streaming message
        setStreamingMessage((prev) => prev + event.data);
      }
    };

    eventSource.onerror = (err) => {
      console.error("SSE error:", err);
      eventSource.close();
      setStreaming(false);
    };

    // Clear the input after sending the message
    setInput('');
  };

  return (
    <Box sx={{ mt: 4 }}>
      <Typography variant="h5" gutterBottom>
        Chat with Electrical AI
      </Typography>
      <Paper sx={{ p: 2, mb: 2, maxHeight: 400, overflowY: 'auto' }}>
        {conversation.map((msg: ChatMessage, index: number) => (
          <motion.div
            key={index}
            initial={{ opacity: 0, y: 10 }}
            animate={{ opacity: 1, y: 0 }}
            transition={{ duration: 0.3 }}
          >
            <Box sx={{ mb: 2, textAlign: msg.role === 'user' ? 'right' : 'left' }}>
              <Typography variant="subtitle2" color="textSecondary">
                {msg.role === 'user' ? 'You' : 'AI'}
              </Typography>
              <Box
                sx={{
                  p: 1,
                  backgroundColor: msg.role === 'user' ? '#dcf8c6' : '#f1f0f0',
                  borderRadius: 2,
                }}
              >
                <ReactMarkdown>{msg.content}</ReactMarkdown>
              </Box>
              {msg.role === 'assistant' && (
                <Button size="small" onClick={() => speakText(msg.content)}>
                  ðŸ”Š Speak
                </Button>
              )}
            </Box>
          </motion.div>
        ))}
        {streaming && streamingMessage && (
          <motion.div
            initial={{ opacity: 0, y: 10 }}
            animate={{ opacity: 1, y: 0 }}
            transition={{ duration: 0.3 }}
          >
            <Box sx={{ mb: 2, textAlign: 'left' }}>
              <Typography variant="subtitle2" color="textSecondary">
                AI (streaming...)
              </Typography>
              <Box sx={{ p: 1, backgroundColor: '#f1f0f0', borderRadius: 2 }}>
                <ReactMarkdown>{streamingMessage}</ReactMarkdown>
              </Box>
              <Button size="small" onClick={() => speakText(streamingMessage)}>
                ðŸ”Š Speak
              </Button>
            </Box>
          </motion.div>
        )}
      </Paper>
      <Box component="form" onSubmit={handleSend} sx={{ display: 'flex', gap: 1 }}>
        <TextField
          fullWidth
          variant="outlined"
          placeholder="Type your message..."
          value={input}
          onChange={(e) => setInput(e.target.value)}
        />
        <Button type="submit" variant="contained" disabled={streaming}>
          {streaming ? 'Streaming...' : 'Send'}
        </Button>
        <Button variant="outlined" onClick={startVoiceInput}>
          ðŸŽ¤ Voice
        </Button>
      </Box>
    </Box>
  );
};

export default ChatInterface;
