// src/ChatInterface.tsx
import React, { useState } from 'react';
import axios from 'axios';

interface ChatMessage {
  role: 'user' | 'assistant';
  content: string;
}

const ChatInterface: React.FC = () => {
  const [input, setInput] = useState<string>('');
  const [conversation, setConversation] = useState<ChatMessage[]>([]);

  const handleSend = async (e: React.FormEvent<HTMLFormElement>) => {
    e.preventDefault();
    if (!input.trim()) return;

    // Append the user's message to the conversation.
    const userMessage: ChatMessage = { role: 'user', content: input };
    setConversation((prev) => [...prev, userMessage]);

    try {
      // Send the query to the backend.
      const response = await axios.post<{ answer: string }>('http://localhost:8000/query', { query: input });
      const assistantMessage: ChatMessage = { role: 'assistant', content: response.data.answer };
      setConversation((prev) => [...prev, assistantMessage]);
      setInput('');
    } catch (error: any) {
      console.error('Error sending query:', error.response ? error.response.data : error);
      const errorMessage: ChatMessage = { role: 'assistant', content: 'Error processing your request.' };
      setConversation((prev) => [...prev, errorMessage]);
    }
  };

  return (
    <div className="chat-container">
      <h2>Chat with Electrical AI</h2>
      <div className="chat-box">
        {conversation.map((msg, index) => (
          <div key={index} className={`chat-message ${msg.role}`}>
            <strong>{msg.role === 'user' ? 'You' : 'AI'}:</strong>
            <div className="message-content">
              {msg.content}
            </div>
          </div>
        ))}
      </div>
      <form onSubmit={handleSend} className="chat-form">
        <input
          type="text"
          placeholder="Type your message..."
          value={input}
          onChange={(e) => setInput(e.target.value)}
        />
        <button type="submit">Send</button>
      </form>
    </div>
  );
};

export default ChatInterface;
