import React, { useState } from 'react';
import axios from 'axios';

interface QueryResponse {
  answer: string;
}

const QueryForm: React.FC = () => {
  const [query, setQuery] = useState<string>('');
  const [answer, setAnswer] = useState<string>('');

  const handleSubmit = async (e: React.FormEvent<HTMLFormElement>) => {
    e.preventDefault();
    try {
      const response = await axios.post<QueryResponse>('http://localhost:8000/query', { query });
      setAnswer(response.data.answer);
    } catch (error: any) {
      console.error('Error querying the AI:', error.response ? error.response.data : error);
      setAnswer('Error processing your request.');
    }
  };

  return (
    <div style={{ margin: '20px' }}>
      <h2>Ask the Electrical AI</h2>
      <form onSubmit={handleSubmit}>
        <input
          type="text"
          placeholder="Type your question here..."
          value={query}
          onChange={(e) => setQuery(e.target.value)}
          style={{ width: '80%', padding: '8px' }}
        />
        <button type="submit" style={{ padding: '8px 16px', marginLeft: '8px' }}>
          Submit
        </button>
      </form>
      {answer && (
        <div style={{ marginTop: '16px' }}>
          <h3>Answer:</h3>
          <p>{answer}</p>
        </div>
      )}
    </div>
  );
};

export default QueryForm;
