// src/components/ChatInterface.tsx
import React, { useState, useRef } from 'react';
import { useAppSelector, useAppDispatch } from '../app/hooks';
import { addMessage, updateLastMessage, ChatMessage } from '../features/chat/chatSlice';
import { Box, Button, TextField, Paper, Typography, Avatar, Stack } from '@mui/material';
import { motion } from 'framer-motion';
import ReactMarkdown from 'react-markdown';
import AccountCircleIcon from '@mui/icons-material/AccountCircle';
import ElectricBoltIcon from '@mui/icons-material/ElectricBolt';

const ChatInterface: React.FC = () => {
  // Retrieve conversation from Redux.
  const conversation = useAppSelector((state) => state.chat.conversation);
  const dispatch = useAppDispatch();

  // Local state for user input and streaming status.
  const [input, setInput] = useState<string>('');
  const [streaming, setStreaming] = useState<boolean>(false);

  // useRef to accumulate live streaming tokens; persists across renders.
  const liveAnswerRef = useRef<string>("");

  // Voice input using the Web Speech API.
  const startVoiceInput = () => {
    const SpeechRecognition =
      (window as any).SpeechRecognition || (window as any).webkitSpeechRecognition;
    if (!SpeechRecognition) {
      alert("Your browser does not support speech recognition.");
      return;
    }
    const recognition = new SpeechRecognition();
    recognition.lang = 'en-US';
    recognition.start();
    recognition.onresult = (event: any) => {
      const transcript = event.results[0][0].transcript;
      setInput(transcript);
    };
  };

  // Voice output using the Web Speech API.
  const speakText = (text: string) => {
    const utterance = new SpeechSynthesisUtterance(text);
    window.speechSynthesis.speak(utterance);
  };

  // Handle sending a message and receiving the SSE streaming response.
  const handleSend = async (e: React.FormEvent<HTMLFormElement>) => {
    e.preventDefault();
    if (!input.trim()) return;

    // Dispatch the user's message.
    dispatch(addMessage({ role: 'user', content: input }));
    // Immediately add an empty assistant message that will be updated live.
    dispatch(addMessage({ role: 'assistant', content: "" }));
    setStreaming(true);
    liveAnswerRef.current = ""; // Reset the live answer accumulator

    console.log("Opening SSE connection for query:", input);
    const eventSource = new EventSource(`http://localhost:8000/query/stream?query=${encodeURIComponent(input)}`);

    eventSource.onopen = () => {
      console.log("SSE connection opened.");
    };

    eventSource.onmessage = (event) => {
      console.log("Received SSE data:", event.data);
      if (event.data.trim() === "[DONE]") {
        eventSource.close();
        setStreaming(false);
        // Do not clear liveAnswerRef.current so that the final answer remains visible.
      } else {
        // Append the incoming token plus a space.
        liveAnswerRef.current += event.data + " ";
        // Update the last assistant message in Redux with the accumulated live answer.
        dispatch(updateLastMessage(liveAnswerRef.current));
      }
    };

    eventSource.onerror = (err) => {
      console.error("SSE error:", err);
      eventSource.close();
      setStreaming(false);
    };

    setInput('');
  };

  // Typing indicator component with animated blinking dots.
  const TypingIndicator: React.FC = () => (
    <Box sx={{ display: 'flex', alignItems: 'center', mt: 1 }}>
      <Typography variant="body2" color="textSecondary" sx={{ mr: 1 }}>
        AI is typing
      </Typography>
      <Stack direction="row" spacing={0.5}>
        {[...Array(3)].map((_, idx) => (
          <Box
            key={idx}
            sx={{
              width: 8,
              height: 8,
              borderRadius: '50%',
              bgcolor: 'primary.main',
              animation: 'blink 1.4s infinite both',
              animationDelay: `${idx * 0.2}s`,
            }}
          />
        ))}
      </Stack>
    </Box>
  );

  return (
    <Box sx={{ mt: 4 }}>
      <Typography variant="h5" gutterBottom>
        Chat with Electrical AI
      </Typography>
      <Paper sx={{ p: 2, mb: 2, maxHeight: 400, overflowY: 'auto' }}>
        {conversation.map((msg: ChatMessage, index: number) => (
          <motion.div
            key={index}
            initial={{ opacity: 0, y: 10 }}
            animate={{ opacity: 1, y: 0 }}
            transition={{ duration: 0.3 }}
          >
            <Stack direction="row" spacing={1} alignItems="flex-start" sx={{ mb: 1 }}>
              <Avatar sx={{ bgcolor: msg.role === 'user' ? 'primary.main' : 'secondary.main' }}>
                {msg.role === 'user' ? <AccountCircleIcon /> : <ElectricBoltIcon />}
              </Avatar>
              <Box
                sx={{
                  p: 1.5,
                  backgroundColor: msg.role === 'user' ? '#dcf8c6' : '#f1f0f0',
                  borderRadius: 2,
                  boxShadow: 1,
                  maxWidth: '80%',
                }}
              >
                <ReactMarkdown>{msg.content}</ReactMarkdown>
              </Box>
            </Stack>
          </motion.div>
        ))}
        {streaming && (
          <motion.div
            initial={{ opacity: 0, y: 10 }}
            animate={{ opacity: 1, y: 0 }}
            transition={{ duration: 0.3 }}
          >
            <Stack direction="row" spacing={1} alignItems="flex-start">
              <Avatar sx={{ bgcolor: 'secondary.main' }}>
                <ElectricBoltIcon />
              </Avatar>
              <Box
                sx={{
                  p: 1.5,
                  backgroundColor: '#f1f0f0',
                  borderRadius: 2,
                  boxShadow: 1,
                  maxWidth: '80%',
                }}
              >
                <ReactMarkdown>{liveAnswerRef.current}</ReactMarkdown>
                <TypingIndicator />
              </Box>
            </Stack>
          </motion.div>
        )}
      </Paper>
      <Box component="form" onSubmit={handleSend} sx={{ display: 'flex', gap: 1 }}>
        <TextField
          fullWidth
          variant="outlined"
          placeholder="Type your message..."
          value={input}
          onChange={(e) => setInput(e.target.value)}
        />
        <Button type="submit" variant="contained" disabled={streaming}>
          {streaming ? 'Streaming...' : 'Send'}
        </Button>
        <Button variant="outlined" onClick={startVoiceInput}>
          ðŸŽ¤ Voice
        </Button>
      </Box>
    </Box>
  );
};

export default ChatInterface;
