// src/FileUpload.tsx
import React, { useState } from 'react';
import axios from 'axios';

interface UploadResponse {
  result: string;
  file_location?: string;
}

interface FileUploadProps {
  endpoint: string; // e.g., "upload_receipt" or "analyze_diagram"
  label: string;
}

const FileUpload: React.FC<FileUploadProps> = ({ endpoint, label }) => {
  const [file, setFile] = useState<File | null>(null);
  const [responseMessage, setResponseMessage] = useState<string>('');

  const handleFileChange = (e: React.ChangeEvent<HTMLInputElement>) => {
    if (e.target.files && e.target.files.length > 0) {
      setFile(e.target.files[0]);
    }
  };

  const handleUpload = async (e: React.FormEvent<HTMLFormElement>) => {
    e.preventDefault();
    if (!file) return;
    const formData = new FormData();
    formData.append('file', file);
    try {
      const response = await axios.post<UploadResponse>(`http://localhost:8000/${endpoint}`, formData, {
        headers: { 'Content-Type': 'multipart/form-data' },
      });
      setResponseMessage(response.data.result);
    } catch (error: any) {
      console.error('Error uploading file:', error.response ? error.response.data : error);
      setResponseMessage('Error processing the file.');
    }
  };

  return (
    <div style={{ margin: '20px' }}>
      <h3>{label}</h3>
      <form onSubmit={handleUpload}>
        <input type="file" onChange={handleFileChange} />
        <button type="submit" style={{ padding: '8px 16px', marginLeft: '8px' }}>Upload</button>
      </form>
      {responseMessage && (
        <div
          style={{
            marginTop: '10px',
            textAlign: 'left',
            whiteSpace: 'pre-wrap',
            background: '#f4f4f4',
            padding: '10px',
            borderRadius: '5px',
            maxWidth: '800px',
            marginLeft: 'auto',
            marginRight: 'auto'
          }}
        >
          <strong>OCR Result:</strong>
          <pre style={{ margin: 0 }}>{responseMessage}</pre>
        </div>
      )}
    </div>
  );
};

export default FileUpload;
