// src/ChatInterface.tsx
import React, { useState } from 'react';
import axios from 'axios';

interface ChatMessage {
  role: 'user' | 'assistant';
  content: string;
}

const ChatInterface: React.FC = () => {
  const [input, setInput] = useState<string>('');
  const [conversation, setConversation] = useState<ChatMessage[]>([]);

  const handleSend = async (e: React.FormEvent<HTMLFormElement>) => {
    e.preventDefault();
    if (!input.trim()) return;

    // Append the user's message to the conversation.
    const newMessage: ChatMessage = { role: 'user', content: input };
    setConversation((prev) => [...prev, newMessage]);

    try {
      // Send the query to your backend endpoint.
      const response = await axios.post<{ answer: string }>('http://localhost:8000/query', { query: input });
      const answerMessage: ChatMessage = { role: 'assistant', content: response.data.answer };
      setConversation((prev) => [...prev, answerMessage]);
      setInput('');
    } catch (error: any) {
      console.error('Error sending query:', error.response ? error.response.data : error);
      const errorMessage: ChatMessage = { role: 'assistant', content: 'Error processing your request.' };
      setConversation((prev) => [...prev, errorMessage]);
    }
  };

  return (
    <div style={{ maxWidth: '800px', margin: 'auto', padding: '20px' }}>
      <h2>Chat with Electrical AI</h2>
      <div style={{
        border: '1px solid #ccc',
        padding: '10px',
        height: '400px',
        overflowY: 'auto',
        background: '#fff',
        borderRadius: '5px'
      }}>
        {conversation.map((msg, index) => (
          <div key={index} style={{
            marginBottom: '10px',
            textAlign: msg.role === 'user' ? 'right' : 'left'
          }}>
            <strong>{msg.role === 'user' ? 'You' : 'AI'}:</strong>
            <div style={{
              backgroundColor: msg.role === 'user' ? '#DCF8C6' : '#F1F0F0',
              padding: '8px',
              borderRadius: '5px',
              display: 'inline-block',
              maxWidth: '70%',
              marginTop: '4px'
            }}>
              {msg.content}
            </div>
          </div>
        ))}
      </div>
      <form onSubmit={handleSend} style={{ marginTop: '10px', display: 'flex' }}>
        <input
          type="text"
          placeholder="Type your message..."
          value={input}
          onChange={(e) => setInput(e.target.value)}
          style={{ flexGrow: 1, padding: '10px', fontSize: '16px' }}
        />
        <button type="submit" style={{ padding: '10px 20px', marginLeft: '10px' }}>Send</button>
      </form>
    </div>
  );
};

export default ChatInterface;
